import React, { useState } from 'react'
import Tooltip from "@mui/material/Tooltip";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import { useDispatchCart } from './Context';


export default function Card(props) {
  let dispatch = useDispatchCart();
  let options = props.options;
  let priceOption = Object.keys(options);
  const [qty, setQty] = useState('1');
  const [size, setSize] = useState("");
  const handleAddToCart = async () => {
    await dispatch({type: "ADD",id:props.foodItem._id,name:props.foodItem.name,price:props.finalPrice,qty:qty,size:size})
  }

  return (
    <div>
      <Tooltip title={props.foodItem.description}>
        <div
          className="card mt-3"
          style={{ width: "18rem", maxHeight: "360px" }}
        >
          <img
            style={{height: "120px", objectFit: "fill"}}
            src={props.foodItem.img}
            class="card-img-top"
            alt="..."
          />
          <div class="card-body">
            <h5 class="card-title">{ props.foodItem.name}</h5>
            <div className="container w-100">
              <select className="m-2 h-100  bg-success rounded" onChange={()=>setQty(e)}>
                {Array.from(Array(6), (e, i) => {
                  return (
                    <option key={i + 1} value={i + 1}>
                      {i + 1}
                    </option>
                  );
                })}
              </select>

              <select className="m-2 h-100 bg-success rounded" onChange={()=> setSize(e.target.value)}>
                {priceOption.map((data) => {
                  return <option key={data} value={data}>{data}</option>
                })}
              </select>
              <div className="d-inline fs-5 h-100">Total price</div>
              <hr></hr>
              <button className={'btn btn-success justify center ms-2'} onClick={handleAddToCart}><AddShoppingCartIcon /></button>
            </div>
          </div>
        </div>
      </Tooltip>
    </div>
  );
}
