import React, { useState } from "react";
import { Link, useNavigate} from "react-router-dom";
import Navbar from "../components/Navbar";

export default function Login() {
  const [credenditals, setcredenditals] = useState({
    email: "",
    password: "",
   
  });
  let navigate = useNavigate()
  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/LoginUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: credenditals.email,
        password: credenditals.password,
      }),
    });
    const json = await response.json()
    console.log(json);

    if (!json.success) {
      alert("Email or Password not correct");
    }
    else if (json.success) {
      localStorage.setItem("userEmail",credenditals.email);
      localStorage.setItem("authToken", json.authToken);
      
      navigate("/");
    }
  };

  const onChange = (e) => {
    setcredenditals({ ...credenditals, [e.target.name]: e.target.value });
  };

  return (
      <div style={{backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <div><Navbar/></div>
    <div>
        <div className="container">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Email:
              </label>
              <input
                type="email"
                className="form-control"
                name="email"
                value={credenditals.email}
                onChange={onChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">
                Password
              </label>
              <input
                type="password"
                className="form-control"
                id="exampleInputPassword1"
                name="password"
                value={credenditals.password}
                onChange={onChange}
              />
            </div>

            <button type="submit" className="btn btn-primary">
              Submit
            </button>
            <Link to="/createuser" className="m-3" >
              New Member?
            </Link>
          </form>
        </div>
      </div>
      </div>
  );
}
